import { Component, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { Observable } from 'rxjs';
import { map, startWith } from 'rxjs/operators';
import { Receita } from './receita.model';
import { ReceitasService } from './receitas.service';

@Component({
  selector: 'app-receitas',
  templateUrl: './receitas.component.html',
  styleUrls: ['./receitas.component.scss'],
})
export class ReceitasComponent implements OnInit {
  receitas: Receita[] = [];
  receitaCtrl = new FormControl();
  filteredReceitas: Observable<Receita[]>;

  constructor(private RS: ReceitasService) {
    this.filteredReceitas = this.receitaCtrl.valueChanges.pipe(
      startWith(''),
      map((receita) =>
        receita ? this._filterReceitas(receita) : this.receitas.slice()
      )
    );
  }

  ngOnInit(): void {
    this.obterReceitas();
  }

  obterReceitas() {
    this.RS.getAll().subscribe((receitas: Receita[]) => {
      this.receitas = receitas;
    });
  }

  private _filterReceitas(value: string): Receita[] {
    const filterValue = value.toLowerCase();
    return this.receitas.filter((receita) =>
      receita.nome.toLowerCase().includes(filterValue)
    );
  }
}
<mat-toolbar>
  <img src="../../assets/logo-coco-bambu-mini.png" alt="" />
  <form class="example-form">
    <mat-form-field style="color: aliceblue" class="example-full-width">
      <mat-label>Prato</mat-label>
      <input
        matInput
        aria-label="Prato"
        [matAutocomplete]="auto"
        [formControl]="receitaCtrl"
      />
      <mat-icon matSuffix>search</mat-icon>
      <mat-autocomplete #auto="matAutocomplete">
        <mat-option
          *ngFor="let receita of filteredReceitas | async"
          [value]="receita.nome"
        >
          <img
            class="example-option-img"
            aria-hidden
            [src]="receita.image.thumb"
            height="25"
          />
          <span>{{ receita.nome }}</span> |
          <small>Tempo: {{ receita.preparo.tempo }}</small>
        </mat-option>
      </mat-autocomplete>
    </mat-form-field>
  </form>
  <div class="col ml-1" style="text-align: center;">
    <img class="row login" src="../../assets/icon-login.png" alt="">
    <br>
    <p>Sair</p>
  </div>
</mat-toolbar>
<div class="receitas">
  <div *ngFor="let receita of receitas">
    <mat-card>
      <mat-card-header>
        <div
          mat-card-avatar
          style="background-image: url('{{ receita.image.thumb }}')"
        ></div>
        <mat-card-title>{{ receita.nome }}</mat-card-title>
        <mat-card-subtitle>{{ receita.desc }}</mat-card-subtitle>
      </mat-card-header>
    </mat-card>
  </div>
</div>
